INSERT INTO `wp_posts` VALUES (187,6,'2019-06-03 09:17:04','2019-06-03 07:17:04','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour cette semaine, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Plat du jour: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi: <em>Salade  au chou kale</em></strong> (courgettes*, tomates cerises*, abricots, salade verte*, chou kale du jardin, parmesan, oeuf° mollet ou poché, sauce caesar végétale aux amandes°, herbes du jardin et de chez Ludivine) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi: Poulet yassa</strong> (Poulet°, citron°, oignons*, riz, mélange de légumes*, cacahuètes° torréfiées)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi: nouilles thaï au boeuf </strong>(nouilles de riz, boeuf grillé émincé et mariné, légumes*, sésames°, cacahuètes torréfiées)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi:  <em>buddha bowl</em> </strong>(au choix: truite fumée au citron ou poulet grillé aux épices, trio de quinoa° ou riz vinaigré, légumes* - crus et rôtis aux épices) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Tarte du moment:</strong> Tarte feuilletée amandes tomates*, velouté de carottes* et patates douces°, salade verte*, feta</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert du moment:</strong> mardi et mercredi: cake à la patate douce/pommes poelées aux épices et citron - jeudi et vendredi: salade de fruits de saison et faisselle façon crumble </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>*produits issus d\'une agriculture locale et raisonnée (Ludivine et Jérôme, Cucurron)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>°produits issus de l\'agriculture biologique</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[\"17\",\"155\",194],\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-3 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/03/salade-abricots-courgette-tomates3-576x1024.jpg\" alt=\"\" data-id=\"17\" data-link=\"http://lherbefolle.net/salade-abricots-courgette-tomates3/\" class=\"wp-image-17\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/05/P5211081-1024x1024.jpg\" alt=\"\" data-id=\"155\" data-link=\"http://lherbefolle.net/index.php/2019/05/22/menu-du-22-mai/non-classe/attachment/olympus-digital-camera-28/\" class=\"wp-image-155\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/P6223634-1024x576.jpg\" alt=\"\" data-id=\"194\" data-link=\"http://lherbefolle.net/index.php/2019/06/03/menu-du-3-juin-2019/non-classe/attachment/p6223634/\" class=\"wp-image-194\"/></figure></li></ul></figure>\n<!-- /wp:gallery -->','Menu du 11 au 14 juin 2019','','publish','open','open','','menu-du-3-juin-2019','','','2021-04-28 11:37:20','2021-04-28 11:37:20','',0,'http://lherbefolle.net/?p=187',0,'post','',0);
INSERT INTO `wp_posts` VALUES (198,6,'2019-06-17 09:20:38','2019-06-17 07:20:38','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, pour préparer une fête de la musique internationale, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Tarte de la semaine: Tarte amandes/tomates</strong>, velouté de carottes aux herbes fraîches, petite salade </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert de la semaine: comme un crumble </strong>aux fraises et cerises</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi, c\'est japonais : Aubergines* à la vapeur</strong>, sésame et oignons de printemps* (servies avec riz et tofu frit)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi, c\'est thaïlandais : Curry laksa </strong>(soupe de nouilles au lait de coco, feuilles de combawa et légumes*) au poulet° ou végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi, c\'est indien: Ratatouille à l\'indienne</strong> (légumes* - pommes de terre, poivrons, haricots verts, tomates, oignons,...), servi avec du riz et un paneer naan</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi, c\'est italien: pâtes aux herbes du jardin</strong>, parmesan et légumes* rôtis </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi, c\'est américain </strong>(ou tout comme)<strong>: burger de patates douces</strong>° et mozzarella d\'Aix</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les plats sont accompagnés de salade (verte ou composée selon les jours et les alliances de goûts)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[203,206,211],\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-3 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20181123_093607-1024x576.jpg\" alt=\"\" data-id=\"203\" data-link=\"http://lherbefolle.net/index.php/2019/06/17/menu-du-17-au-19-juin-2019/non-classe/attachment/20181123_093607/\" class=\"wp-image-203\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20190613_090521-1024x1024.jpg\" alt=\"\" data-id=\"206\" data-link=\"http://lherbefolle.net/index.php/2019/06/17/menu-du-17-au-19-juin-2019/non-classe/attachment/20190613_090521/\" class=\"wp-image-206\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20190618_122109-1024x1024.jpg\" alt=\"\" data-id=\"211\" data-link=\"http://lherbefolle.net/index.php/2019/06/17/menu-du-17-au-19-juin-2019/non-classe/attachment/20190618_122109/\" class=\"wp-image-211\"/></figure></li></ul></figure>\n<!-- /wp:gallery -->','Menu du 17 au 19 juin 2019','','publish','open','open','','menu-du-17-au-19-juin-2019','','','2021-04-28 11:39:26','2021-04-28 11:39:26','',0,'http://lherbefolle.net/?p=198',0,'post','',0);
INSERT INTO `wp_posts` VALUES (215,6,'2019-06-24 21:39:21','2019-06-24 19:39:21','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour vous soutenir dans cette semaine caniculaire, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi et Mardi, c\'est salade</strong> : <em> Salade de crudités (</em>courgettes, carottes, salade verte, abricots, concombre, herbes du jardin), mozzarella, truite fumée ou poulet rôtis, sauce au yaourt grec et à la menthe</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi,  c\'est salade aussi : </strong><em>Salade thaï</em> (légumes crus marinés, nouilles de riz, herbes fraîches) au bœuf ou végétarienne (tofu grillé)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi, c\'est salade :</strong> Trio de quinoa, légumes crus et cuits, poulet grillé aux épices</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi, c\'est salade aussi :</strong> <em>Aubergines vapeur </em>à la japonaise, tofu au miso, nouilles soba - accompagné de salade verte aux sésame</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert de la semaine</strong>: <em>Salade de fraises et abricots </em>au basilic, crème au chocolat ou tartelettes crème d\'amandes/abricots</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[86,222,221],\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-3 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/04/P4110503-1024x576.jpg\" alt=\"\" data-id=\"86\" data-link=\"http://lherbefolle.net/index.php/2019/04/17/menu-du-17-avril-2019/non-classe/attachment/p4110503/\" class=\"wp-image-86\"/><figcaption class=\"blocks-gallery-item__caption\">petit rayon de soleil matinal dans le jardin de l\'HerbeFolle</figcaption></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20190624_121454-e1561444813852-1024x1024.jpg\" alt=\"\" data-id=\"222\" data-link=\"http://lherbefolle.net/?attachment_id=222\" class=\"wp-image-222\"/><figcaption class=\"blocks-gallery-item__caption\">La salade de l\'Herbe Folle</figcaption></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20190618_100858-e1561444776913-1024x1024.jpg\" alt=\"\" data-id=\"221\" data-link=\"http://lherbefolle.net/?attachment_id=221\" class=\"wp-image-221\"/><figcaption class=\"blocks-gallery-item__caption\">Sauna d\'Aubergines</figcaption></figure></li></ul></figure>\n<!-- /wp:gallery -->','Menu du 24 au 28 juin','semaine fraîche pour pour résister à la vague de chaleur!\nSalade tous les jours à l\'Herbe Folle','publish','open','open','','menu-du-24-au-28-juin','','','2021-04-28 11:49:01','2021-04-28 11:49:01','',0,'http://lherbefolle.net/?p=215',0,'post','',0);
INSERT INTO `wp_posts` VALUES (231,6,'2019-07-02 10:26:22','2019-07-02 08:26:22','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Par solidarité avec une terre qui n\'en finit plus de s’échauffer, l\'Herbe Folle est montée en température elle aussi.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Elle ne pourra régaler vos papilles avant jeudi 4 juillet et vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi, c\'est poisson et orient: </strong>Kebab de cabillaud, sauce au yaourt et salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi, c\'est courgettes, Grèce et Italie: </strong>Parpadelles aux courgettes et Feta</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert du moment</strong>: Salade de fruits frais à la menthe, cake au citron</p>\n<!-- /wp:paragraph -->','Semaine du 1er au 5 juillet','kebab de poisson\nparpadelles aux courgettes et feta\ncake au citron et salade de fruits','publish','open','open','','semaine-du-1er-au-5-juillet','','','2021-04-28 11:50:34','2021-04-28 11:50:34','',0,'http://lherbefolle.net/?p=231',0,'post','',0);
INSERT INTO `wp_posts` VALUES (237,6,'2019-07-18 11:15:02','2019-07-18 09:15:02','<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour cette première semaine de vacances d\'été, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi, c\'est thailandais</strong>: Salade Thaï aux crevettes et mangue</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi, c\'est végétarien</strong>: Salade sucrée/salée aux abricots, parmesan et roquette</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi, c\'est italien</strong>: Pâtes aux courgettes, pignons, parmesan - et saucisse italienne aux 5 poivres (porc Label Rouge du Sud-Ouest)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi, c\'est japonais</strong>: Aubergines cuites à la vapeur, à l\'huile de sésame, omelette japonaise aux oignons de printemps (servi avec riz cambodgien et salade)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi, c\'est réunionnais:</strong> Rougaille saucisses (servi avec riz basmati et accompagné de salade).</p>\n<!-- /wp:paragraph -->','Semaine du 8 au 12 juillet','','publish','open','open','','semaine-du-8-au-12-juillet','','','2021-04-28 11:51:58','2021-04-28 11:51:58','',0,'http://lherbefolle.net/?p=237',0,'post','',0);
INSERT INTO `wp_posts` VALUES (240,6,'2019-07-18 16:07:28','2019-07-18 14:07:28','<!-- wp:paragraph -->\n<p>L\'Herbe Folle vous propose cette semaine, :</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi</strong>: Salade d\'été pastèque, pignons, parmesan, jambon cru des alpes (porc de camargue Label Rouge)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi</strong>: Boeuf grillé mariné à la japonaise, soba et légumes vapeur</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi</strong>: pâtes au citron, parmesan, courgettes et saucisse de camargue (porc label rouge du Sud-ouest)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi</strong>: Poulet Yassa (citron-oignons), servi avec du riz cambodgien et accompagné de salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi</strong>: Soupe thaï au lait de coco</p>\n<!-- /wp:paragraph -->','semaine du 15 au 19 juillet','','publish','open','open','','semaine-du-15-au-19-juillet','','','2021-04-28 11:53:14','2021-04-28 11:53:14','',0,'http://lherbefolle.net/?p=240',0,'post','',0);
INSERT INTO `wp_posts` VALUES (242,6,'2019-07-22 07:58:16','2019-07-22 05:58:16','<!-- wp:paragraph -->\n<p>Bonjour à tous! Pour contribuer activement à cette seconde phase caniculaire, l\'Herbe Folle vous propose un menu chaud/froid:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Plats du jour: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Chaud</strong>: <strong><em>Rougaille saucisses</em></strong> (saucisses fraîches et fumées de porc Label Rouge du Sud-ouest, oignons*, carottes* - servi avec du riz du Cambodge et accompagné de salade*)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Froid</strong>: <strong><em>Poke bowl </em></strong>à la truite fumée citronnée ou au tofu° grillé (carottes* et courgettes* marinées, aubergines* vapeur à l\'huile de sésame, mangue°(selon disponibilités), riz vinaigré, salade*)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert</strong>: faisselle et fruits de saison façon crumble</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les commandes sont ouvertes au 04.88.29.50.99!</p>\n<!-- /wp:paragraph -->','Menu du 22 au 26 juillet','','publish','open','open','','menu-du-22-au-26-juillet','','','2021-04-28 11:55:23','2021-04-28 11:55:23','',0,'http://lherbefolle.net/?p=242',0,'post','',0);
INSERT INTO `wp_posts` VALUES (250,6,'2019-07-30 08:24:36','2019-07-30 06:24:36','<!-- wp:paragraph -->\n<p>Bonjour à tous!\nAvant une trêve estivale qui s\'étendra du 1er au 16 août et pour terminer la saison en légèreté, c\'est une semaine de salades que vous propose l\'Herbe Folle.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>Lundi:</strong> salade de légumes grillés et riz sauvage</li><li><strong>Mardi</strong>: salade japonaise au boeuf mariné</li><li><strong>Mercredi</strong>:  </li><li><strong>Jeudi</strong>: salade thaï végétarienne à la mangue</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Menu du 29 au 1er août','','publish','open','open','','menu-du-29-au-1er-aout','','','2021-04-28 11:56:11','2021-04-28 11:56:11','',0,'http://lherbefolle.net/?p=250',0,'post','',0);
INSERT INTO `wp_posts` VALUES (265,6,'2019-08-08 16:24:17','2019-08-08 14:24:17','<!-- wp:paragraph -->\n<p>L\'Herbe Folle se pose en pays capiste pour quelques jours. Le temps de s\'imprégner d\'air océanien, de rissoler au beurre demi-sel en sifflotant quelques chants marins.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>On se retrouve bientôt !  Bonnes vacances à tous.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Trêve estivale!!','','publish','open','open','','treve-estivale','','','2021-04-28 11:57:32','2021-04-28 11:57:32','',0,'http://lherbefolle.net/?p=265',0,'post','',0);
INSERT INTO `wp_posts` VALUES (271,6,'2019-08-20 12:15:02','2019-08-20 10:15:02','<!-- wp:paragraph -->\n<p>Bonjour à tous! Et bon retour pour ceux qui, comme l\'Herbe Folle, se sont mis au frais la première quinzaine d\'août. Nous vous proposons cette semaine:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi: Salade grecque (tomates, concombre, feta, oignons) servie avec du pain pita et de la crème de tomates séchées, de la salade verte et de la tapenade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi: lamelles d\'encornets à la tomate et aux courgettes, servi avec du riz safrané</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Aubergines vapeur à la japonaise et nouilles soba, tofu grillé aux sésames</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Pâtes aux parmesan, tomates sauce vierge et crevettes, accompagnées de salade verte</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: fermeture</p>\n<!-- /wp:paragraph -->','Semaine du 19 au 22 août','','publish','open','open','','semaine-du-19-au-22-aout','','','2021-04-28 11:58:27','2021-04-28 11:58:27','',0,'http://lherbefolle.net/?p=271',0,'post','',0);
INSERT INTO `wp_posts` VALUES (278,6,'2019-08-28 09:19:38','2019-08-28 07:19:38','<!-- wp:paragraph -->\n<p>Lundi: Burger au poulet/aubergine grillée/tomates confites, servi avec salade verte et coleslaw</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi: curry de crevettes, légumes marinés et riz soja/sésames</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: parpadelles aux courgettes/tomates confites/olives/feta</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>jeudi: tarte vegan tomates/amandes, salade verte/noix et graines ou curry de cabillaud, riz</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>vendredi: salade grecque (tomates, feta, oignons nouveaux)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Dessert de la semaine: salade de nectarines et pêches, yaourt grec, gâteau myrtilles/citron/amandes </p>\n<!-- /wp:paragraph -->','Semaine du 26 au 30 août','','publish','open','open','','semaine-du-26-au-30-aout','','','2021-04-28 11:59:48','2021-04-28 11:59:48','',0,'http://lherbefolle.net/?p=278',0,'post','',0);
INSERT INTO `wp_posts` VALUES (286,6,'2019-09-03 08:51:53','2019-09-03 06:51:53','<!-- wp:paragraph -->\n<p>C\'EST LA RENTREE!!!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>L\'Herbe Folle fait, cette semaine, la rentrée scolaire et se consacrera à nourrir nos chères têtes blondes, brunes, rousses, châtains, auburns, bref, multicolores, pour les aider à commencer l\'année scolaire avec une énergie débordante et les neurones bien chargés.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>La rentrée des grands se fera donc la semaine prochaine, mais n\'hésitez pas à nous appeler pour réserver dès à présent votre menu du midi.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Nous vous proposons, pour la semaine du 9 au 13 septembre:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi: Pâtes</strong> au citron, feta, tomates, aubergines, herbes fraîches du jardin</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi: Donburi</strong> au poulet ou au tofu (bol de riz, poulet ou tofu mariné, légumes grillés)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi: Tarte</strong> tomates/amandes, légumes grillés et salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi: Ceviche </strong>de cabillaud au lait de coco et vanille, salades</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi: Aubergine</strong> vapeur à la japonaise, nouilles soba, salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert de la semaine:</strong> Salade de fruits de saison et tarte au citron ou au chocolat</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> </p>\n<!-- /wp:paragraph -->','Semaine du 2 au 6 septembre','','publish','open','open','','semaine-du-2-au-6-septembre','','','2021-04-28 12:01:58','2021-04-28 12:01:58','',0,'http://lherbefolle.net/?p=286',0,'post','',0);
INSERT INTO `wp_posts` VALUES (291,6,'2019-10-02 13:18:21','2019-10-02 11:18:21','<!-- wp:paragraph -->\n<p>Et ferme un temps pour bientôt vous accueillir à sa table !</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','L\'HERBE FOLLE GRANDIT','','publish','open','open','','lherbe-folle-grandit','','','2021-04-28 12:02:42','2021-04-28 12:02:42','',0,'http://lherbefolle.net/?p=291',0,'post','',0);
INSERT INTO `wp_posts` VALUES (294,6,'2019-12-12 14:28:18','2019-12-12 13:28:18','<!-- wp:paragraph -->\n<p>Bienvenue !!   l\'Herbe Folle vous ouvre les portes de sa maison et vous accueille  du mercredi au vendredi, à partir de midi.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Installés bien au chaud dans notre salon, ou au soleil sur la terrasse, dans le jardin, venez déguster la cuisine de l\'Herbe Folle, une cuisine de saison mitonnée avec tendresse à partir de produits locaux, en agriculture biologique ou raisonnée.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Formule unique entrée-plat-dessert à 13 euros - en déclinaison végétarienne à la demande. Formule enfant à 7 euros.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Nous vous proposons cette semaine:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 11 décembre: Velouté de courge/huile aux épices - Poulet Yassa</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 12 décembre: Velouté de lentilles corail et lait de coco - Curry de légumes et naan au fromage</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 13 décembre: Velouté de butternut à la citronnelle et lait de coco - Salade Thaï au boeuf</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: Gâteau myrtilles/amandes/citron ou cake ananas/noix de coco/orange/chololat -  pommes et poires caramélisées et crème fouettée craquante</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Ouverture de la cantine et du café d\'Hôte de l\'Herbe Folle','','publish','open','open','','ouverture-de-la-cantine-et-du-cafe-dhote-de-lherbe-folle','','','2021-04-28 12:04:09','2021-04-28 12:04:09','',0,'http://lherbefolle.net/?p=294',0,'post','',0);
INSERT INTO `wp_posts` VALUES (307,6,'2019-12-29 12:29:39','2019-12-29 11:29:39','<!-- wp:paragraph -->\n<p>Tous nos voeux pour cette nouvelle année! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Après la trêve des confiseurs, l\'Herbe Folle vous propose de nous retrouver dès le 2 janvier pour une pause déjeuner détoxifiante autour de bouillons et soupes de saison.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 2 janvier: Ramen aux crevettes ou au poulet - soupe de nouilles japonaise (bouillon dashi/bouillon poulet-soja et nouilles maison, crevettes biologiques/poulet biologique, épinards, carottes, courge...)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 3 janvier: Soupe au céleri et saumon mariné. Pain aux céréales, fromage frais</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts du jour: tarte fine aux clémentines ou fromage frais battu au miel de lavandes d\'Aurel et fruits de saison poêlés</p>\n<!-- /wp:paragraph -->','Réouverture de l\'Herbe Folle le 2 janvier','','publish','closed','open','','reouverture-de-lherbe-folle-le-2-janvier','','','2021-04-28 12:05:37','2021-04-28 12:05:37','',0,'http://lherbefolle.net/?p=307',0,'post','',0);
INSERT INTO `wp_posts` VALUES (313,6,'2020-01-08 10:33:56','2020-01-08 09:33:56','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour tenir cette semaine de rentrée tout en récupérant des vacances, l\'Herbe Folle vous propose des plats régénérants et purifiants.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 08 janvier: Soupe de courge au lait de coco - curry au poulet et légumes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 09 janvier: Soupe au céleri - bucatini aux noix/feta/courge rôtie/blettes poêlées</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 10 janvier: Soupe miso - Donburi (bol de riz vinaigré au poulet et oeufs battus) et légumes marinés</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts du moment: Tarte fine aux oranges et compotée poires/pommes/ fromage grec battu au miel</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flan au thé matcha et pommes/poires poêlées au miel (vendredi)</p>\n<!-- /wp:paragraph -->','Semaine du 06 au 10 janvier 2020','','publish','closed','open','','semaine-du-06-au-10-janvier-2020','','','2021-04-28 12:06:15','2021-04-28 12:06:15','',0,'http://lherbefolle.net/?p=313',0,'post','',0);
INSERT INTO `wp_posts` VALUES (318,6,'2020-01-14 15:33:38','2020-01-14 14:33:38','<!-- wp:paragraph -->\n<p>Pour cette semaine de mi-janvier, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 13 janvier: Velouté de panais - tyrokafteri (feta* marinée et grillée), pain maison, carottes vapeur, salade  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 15 janvier: Soupe de céleri° - poulet* rôti au gingembre et à l\'ail, pommes de terre, navets et carottes grillées°</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 16 janvier: Soupe de courge butternut° au lait de coco* - curry de légumes°</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 17 janvier: Ramen maison (nouilles et soupe) au filet mignon de porc° mariné</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts du moment: gâteau citron/myrtilles - yaourt grec fouetté au miel - flan au thé matcha </p>\n<!-- /wp:paragraph -->','Semaine du 13 au 17 janvier 2020','','publish','closed','open','','semaine-du-13-au-17-janvier-2020','','','2020-01-14 15:33:38','2020-01-14 14:33:38','',0,'https://lherbefolle.net/?p=318',0,'post','',0);
INSERT INTO `wp_posts` VALUES (322,6,'2020-01-20 21:38:48','2020-01-20 20:38:48','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour cette avant-dernière semaine de janvier, L\'Herbe Folle vous fait encore voyager!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi, Italie: Soupe de céleri - Bucatini feta/crevettes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi: c\'est le jour des petits! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi, Maroc: soupe façon Harira - Tajine d\'agneau au fenouil</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi, Japon: soupe miso et tofu - Katsudon (bol de riz vinaigré, filet mignon de porc mariné et pané, légumes cuits et crus)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi, retour en orient : soupe de courge à la coriandre - boulettes de merlan safranée  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts du moment: lundi- gâteau pommes/poires; mercredi- salade d\'oranges; jeudi-tarte au citron et au thé; vendredi-flan agrumes</p>\n<!-- /wp:paragraph -->','Semaine du 20 au 24 janvier 2020','','publish','closed','open','','semaine-du-20-au-25-janvier-2020','','','2021-04-28 12:09:02','2021-04-28 12:09:02','',0,'https://lherbefolle.net/?p=322',0,'post','',0);
INSERT INTO `wp_posts` VALUES (330,6,'2020-03-03 17:57:34','2020-03-03 16:57:34','<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe Folle vous propose en entrée de petites déclinaisons autour de la courge:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 2 mars: Soupe de courge butternut / poulet à l\'orange amère et légumes grillés</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 3 mars: c\'est le jour des petits! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 4 mars: Soupe de courge au safran et à l\'orange / couscous végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 5 mars: Soupe de courge et lentilles corail au lait de coco / curry de légumes </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 6 mars: Velouté de butternut et comté / bucatini tomate, fenouil</p>\n<!-- /wp:paragraph -->','Semaine du 2 au 6 mars 2020','','publish','closed','open','','semaine-du-2-au-6-mars-2020','','','2021-04-28 12:10:09','2021-04-28 12:10:09','',0,'https://lherbefolle.net/?p=330',0,'post','',0);
INSERT INTO `wp_posts` VALUES (332,6,'2020-02-23 11:04:06','2020-02-23 10:04:06','<!-- wp:paragraph -->\n<p>Cette semaine, L\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 24 février: Dahl de lentilles corail et curry de cabillaud et légumes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 25 février: Velouté de céleri et bucatini feta/fenouil </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 26 février: Velouté de courge butternut et cacahuètes et poulet yassa</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 27 février: Soupe de carottes et gingembre et crevettes façon thaï </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 28 février: Soupe de ramen à la courge rôtie et au tofu</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: Gâteau myrtilles-citron et mi-cuit mi-cru de pommes-poires. Crème brulée à la cardamome</p>\n<!-- /wp:paragraph -->','Semaine du 24 au 28 février 2020','','publish','closed','open','','semaine-du-24-au-28-fevrier-2020','','','2021-04-28 12:09:49','2021-04-28 12:09:49','',0,'https://lherbefolle.net/?p=332',0,'post','',0);
INSERT INTO `wp_posts` VALUES (334,6,'2020-02-16 11:14:16','2020-02-16 10:14:16','<!-- wp:paragraph -->\n<p>Lundi 17 février: Velouté de potimarron aux châtaignes et poulet rôti à l\'orange amère</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 18 février: c\'est le jour des petits!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 19 février: Velouté de céleri au parmesan et pizzeta aux légumes grillés et à la mozzarella d\'Aix en Provence</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 20 février: Soupe de courge butternut au gingembre / Gardianne de Taureau</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 21 février: Soupe miso et poisson grillé à la japonaise</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: Comme un cheesecake pommes-poires</p>\n<!-- /wp:paragraph -->','Semaine du 17 au 21 février 2020:','','publish','closed','open','','semaine-du-17-au-21-fevrier-2020','','','2021-04-28 12:09:26','2021-04-28 12:09:26','',0,'https://lherbefolle.net/?p=334',0,'post','',0);
INSERT INTO `wp_posts` VALUES (338,6,'2020-03-26 11:34:58','2020-03-26 10:34:58','<!-- wp:paragraph -->\n<p>Chers amis, nous sommes de tout coeur avec les équipes du Centre Hospitalier ou d\'ailleurs, qui luttent sans répit contre la maladie et pour préserver des-nos vies.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Nous avons dû fermer nos portes, et tentons d\'organiser dans le quartier des petites initiatives solidaires pour faciliter la vie , achats groupés de fruits et légumes et livraisons dans le jardin de l\'Herbe Folle, par exemple.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Je n\'ai pas mis en place de service de vente à emporter pour le moment, ne sachant encore les possibilités de mes fournisseurs dans cette situation de crise, mais vous tiendrai au courant s\'il m\'est possible de proposer un tel service.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>D\'ici là, nous vous souhaitons beaucoup de courage, de persévérance, de solidarité et de petits moments de joie et de paix.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A bientôt</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Tous ensemble','','publish','closed','open','','tous-ensembles-dans-lepreuve','','','2021-04-28 12:10:38','2021-04-28 12:10:38','',0,'https://lherbefolle.net/?p=338',0,'post','',0);
INSERT INTO `wp_posts` VALUES (357,6,'2020-03-30 17:00:31','2020-03-30 15:00:31','<!-- wp:paragraph -->\n<p>Pour soutenir les personnels de l\'hôpital Pasteur, L\'Herbe Folle devient végétarienne et refait bouillir ses marmites du lundi au samedi - sur réservation la veille:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 30 mars: Soupe de courge butternut et salade thaïe aux légumes et fruits locaux et de saison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 31 mars: Soupe de carotte aux épices et Mafé végétarien (légumes grillés et mijotés, soja, sauce cacahuètes, riz) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 1er avril: Velouté de poireaux et pâtes feta/sauce tomate/fenouil</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 2 avril: Légumes grillés au curry de madras</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 3 avril: Soupe à l\'olive et riz citronné au parmesan, légumes racine</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Samedi 4 avril: Petite salade de saison et pizzetta légumes, mozzarella d\'Aix en Provence</p>\n<!-- /wp:paragraph -->','Semaine du 30 mars au 5 avril','','publish','closed','open','','semaine-du-30-mars-au-5-avril','','','2021-04-28 12:14:18','2021-04-28 12:14:18','',0,'https://lherbefolle.net/?p=357',0,'post','',0);
INSERT INTO `wp_posts` VALUES (370,6,'2020-04-06 14:55:42','2020-04-06 12:55:42','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En cette quatrième semaine de confinement, l\'Herbe Folle vous propose, pour changer, une formule plat-dessert:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 06: Maffé végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 07 avril : Orecchiette citron/parmesan/fenouil/poireaux</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 08 avril: Curry de légumes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 09 avril: Pissaladière et salade </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 10 avril:  Rougaille végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Samedi 11 avril: Asperges juste cuites, salade mixte de saison et oeuf poché; pain maison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: yaourt grec frappé au miel, dés de pomme; gâteau au chocolat, crème au yaourt fouetté; gâteau amandes/citron, julienne de pommes</p>\n<!-- /wp:paragraph -->','Semaine du 06 au 12 avril','','publish','closed','open','','semaine-du-06-au-12-avril','','','2021-04-28 12:14:35','2021-04-28 12:14:35','',0,'https://lherbefolle.net/?p=370',0,'post','',0);
INSERT INTO `wp_posts` VALUES (401,6,'2020-04-15 19:20:19','2020-04-15 17:20:19','<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 12 avril: Soupe de courge butternut et salade thaïe aux légumes et fruits locaux et de saison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 13 avril: Soupe de carotte aux épices et Mafé végétarien (légumes grillés et mijotés, soja, sauce cacahuètes, riz) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 14 avril: Velouté de poireaux et pâtes feta/sauce tomate/fenouil</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 15 avril: Légumes grillés au curry de madras et yaourt, granola et fruits frais (le tout maison, bien entendu)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 17 avril: Riz citronné au parmesan, légumes racine, fenouil grillé - gâteau chocolat et tranches de kiwi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Samedi 18 avril: Petite salade de saison et pizzetta légumes, mozzarella d\'Aix en Provence</p>\n<!-- /wp:paragraph -->','Semaine du 12 au 18 avril','','publish','closed','open','','semaine-du-12-au-18-avril','','','2021-04-28 12:14:47','2021-04-28 12:14:47','',0,'https://lherbefolle.net/?p=401',0,'post','',0);
INSERT INTO `wp_posts` VALUES (405,6,'2020-04-24 15:51:32','2020-04-24 13:51:32','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour la semaine du 1er mai, l\'Herbe Folle vous propose, pour changer, une formule plat-dessert:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 27: Maffé végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 28 avril : Orecchiette citron/parmesan/fenouil/poireaux, salade de saison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 29 avril: Curry de légumes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 30 avril: Rougail végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 1er mai:  Pissaladière et salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Samedi 2 mai: Asperges juste cuites, salade mixte de saison et oeuf poché; pain maison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: yaourt grec frappé au miel, dès de pomme; gâteau au chocolat, crème au yaourt fouetté; gâteau amandes/citron, julienne de pommes</p>\n<!-- /wp:paragraph -->','Semaine du 27 au 2 mai','','publish','closed','open','','405-2','','','2021-04-28 12:15:07','2021-04-28 12:15:07','',0,'https://lherbefolle.net/?p=405',0,'post','',0);
INSERT INTO `wp_posts` VALUES (418,6,'2020-05-03 10:53:46','2020-05-03 08:53:46','<!-- wp:paragraph -->\n<p>En cette première semaine de mai, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>lundi 4 mai: Orrechiete feta/tomate/fenouil, légèrement épicées à la harissa</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>mardi 5 mai:  Bol complet (carottes rôties, salade, légumes lactofermentés, panisses (en fonction des disponibilités), pois chiches marinés, graines...)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>mercredi 6 mai: Mafé de patates douces (plat sénégalais à base de purée de cacahuètes)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>jeudi 7 mai: Soupe thaï lentilles corail/lait de coco, pains vapeur aux épices, salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>vendredi 8 mai: Salade grecque tomates/feta/olives/oignons verts, tzaziki et pain pita</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>samedi 9 mai: pizzetta mozzarella/ légumes grillés, salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: Gâteau citron/myrtilles/amandes et yaourt grec à la menthe ; brownie chocolat/amandes caramélisées, julienne de fruits de saison</p>\n<!-- /wp:paragraph -->','Semaine du 4 au 9 mai','','publish','closed','open','','semaine-du-4-au-9-mai','','','2021-04-28 12:15:49','2021-04-28 12:15:49','',0,'https://lherbefolle.net/?p=418',0,'post','',0);
INSERT INTO `wp_posts` VALUES (424,6,'2020-05-10 11:40:44','2020-05-10 09:40:44','<!-- wp:paragraph -->\n<p>Bonjour à tous! En cette première semaine de déconfinement, L\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 11 mai:  Orrechiette (ou bucatini, selon l\'humeur) au citron et ricotta, parmesan, courgettes et asperges, et petite salade de pousses d\'épinards, tomates ananas/basilic<em>(gluten, lactose)</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 12 mai: Tout vert ou presque - petits pois, manges-tout, courgettes grillées, asperges vapeur, kiwi, tzaziki... et riz rouge aux épices. Pain maison à l\'épeautre <em>(gluten, lactose)</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 13 mai: Tout rouge ou presque - tomates/herbes fraîches, gâteau betteraves/parmesan/feta (bio), houmous rouge, carottes rôties à la cannelle... et salade <em>(sésame, gluten, oeuf)</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 14 mai: Salade Thaï de nouilles aux crevettes <em>(produits issus de la mer, soja, sésame)</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 15 mai: Aïoli <em>(poisson, oeuf)</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: fondant au chocolat -- chou au citron <em>(gluten, oeufs, lactose)</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Tous les légumes et fruits sont issus d\'une agriculture locale et raisonnée ou biologique et française (ou européenne pour les citrons, de Sicile). Le poisson est FSC, les crevettes d\'origine biologique.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Allergènes: ils sont en italique après les plats.</p>\n<!-- /wp:paragraph -->','Semaine du 11 au 15 mai','','publish','closed','open','','semaine-du-11-au-15-mai','','','2021-04-28 12:21:46','2021-04-28 12:21:46','',0,'https://lherbefolle.net/?p=424',0,'post','',0);
INSERT INTO `wp_posts` VALUES (433,6,'2020-05-16 19:04:37','2020-05-16 17:04:37','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En cette semaine de rentrée équilibriste, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi: Comme un petit coin d\'Italie (fenouil et courgettes grillées, croûtons de ciabatta, lentilles aux herbes fraîches, au miel et amandes grillées)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi: Un parfum d\'Iran (reshteh (nouilles iraniennes) aux courgettes grillées et épicées et au yaourt à la menthe)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: le Japon à nos portes - SabaDonburi (maquereau laqué et légumes grillés sur un grand bol de riz)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Un petit tour par l\'Inde (dahl de lentilles, curry de légumes, naan à la coriandre)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: On rentre par la Thaïlande - Salade Thaï (est-ce besoin d\'en rajouter?)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: Tarte fine aux pommes  et chocolat façon mille feuilles- yaourt grec battu au miel, fruits de saison et granola maison - </p>\n<!-- /wp:paragraph -->','Semaine du 18 au 22 mai','','publish','closed','open','','semaine-du-18-au-22-mai','','','2021-04-28 12:24:31','2021-04-28 12:24:31','',0,'https://lherbefolle.net/?p=433',0,'post','',0);
INSERT INTO `wp_posts` VALUES (439,6,'2020-05-24 21:19:54','2020-05-24 19:19:54','<!-- wp:paragraph -->\n<p>bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour cette dernière semaine du mois de mai, L\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 25 mai: soupe glacée aux courgettes et fromage de chèvre frais, pain frit à l\'ail et aux herbes , salade et graines</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 26 mai:  Petit tour par le Mexique avec un Chili sin carne</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 27 mai: L\'Herbe Folle retourne en Thaïlande et propose un Pad Thaï</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 28 mai: l\'Italie revisitée avec un risotto d\'orge perlé aux légumes rôtis</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 29 mai: une petite pause japonaise aux légumes frais et croquants parfumés au gingembre, des nouilles légèrement relevées, un effiloché de poisson (cru ou cuit, nous verrons) et des herbes du jardin</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: je ne sais pas encore... il fait chaud, aussi il y aura des fruits frais et probablement un dessert au chocolat  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!</p>\n<!-- /wp:paragraph -->','Semaine du 25 au 29 mai','','publish','closed','open','','semaine-du-24-au-28-mai','','','2021-04-28 12:24:53','2021-04-28 12:24:53','',0,'https://lherbefolle.net/?p=439',0,'post','',0);
INSERT INTO `wp_posts` VALUES (444,6,'2020-06-01 06:52:52','2020-06-01 04:52:52','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>l\'Herbe Folle pourra très bientôt vous accueillir à nouveau dans son jardin!  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mais en attendant, je vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 2 juin: Le tout rouge ou presque de l\'Herbe Folle (gâteau de betteraves au parmesan et à la feta, légumes crus et légumes grillés, salade, tzaziki, ...)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 3 juin: L\'escale réunionnaise! Rougail végétarien (au Tofu, fumé maison et aux légumes grillés)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 4 juin: Petite pause asiatique, avec la Salade Thaï du moment</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 5 juin: Fin de semaine à Hawaï... comme un Poke bowl (au poisson ou végétarien)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: Encore en réflexion, un chou ou un éclair au chocolat et aux noisette accompagné d\'une salade de fruits frais semble pointer son nez...</p>\n<!-- /wp:paragraph -->','semaine du 2 au 5 juin','','publish','closed','open','','semaine-du-2-au-5-juin','','','2021-04-28 12:26:02','2021-04-28 12:26:02','',0,'https://lherbefolle.net/?p=444',0,'post','',0);
INSERT INTO `wp_posts` VALUES (448,6,'2020-06-06 19:17:26','2020-06-06 17:17:26','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>L\'Herbe Folle vous propose cette semaine:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 8 juin: déconfinement méditerranéen avec une salade lentilles au miel/carottes épicées/feta/herbes et un tzaziki, accompagnés de quelques olives, tomates et pain maison à l\'huile d\'olives</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 9 juin: on élève le débat, avec les épices d\'un curry coco de légumes et lentilles corail</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 10 juin: on retourne du côté de la méditerranée avec des kebabs de poisson aux herbes fraiches</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 11 juin: on y est bien, on y reste mais on se balade... Pasta a la norma et petite salade de saison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 12 juin: une excursion en pays carné avec une salade Thaï au bœuf épicé</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les desserts de la semaine: le chef y réfléchit... il y aura du citron, des abricots, de la menthe probablement...</p>\n<!-- /wp:paragraph -->','Semaine du 8 au 12 juin','','publish','closed','open','','semaine-du-8-au-12-juin','','','2021-04-28 12:26:25','2021-04-28 12:26:25','',0,'https://lherbefolle.net/?p=448',0,'post','',0);
INSERT INTO `wp_posts` VALUES (454,6,'2020-06-14 19:21:38','2020-06-14 17:21:38','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe Folle aime bien retrouver quelques recettes éprouvées.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 15: Les cuisines fleureront bon les parfums perses avec des pâtes à l\'iranienne (aux aubergines, carottes rôties, amandes grillées, yaourt et menthe)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 16: Nous retrouvons avec plaisir le bo-bowl \"tout vert ou presque\", florilèges de légumes crus, grillés, frits, quelques panisses, de la salade,des herbes de Provence, de belles tomates (nous verrons si les tigrées vertes sont déjà mûres)  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 17: L\'incontournable petit voyage en Asie avec une Soupe Thaï de lentilles corail, accompagnée de buns oignons confits et salade  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 18: Rougail végétarien (au tofu fumé maison) ou saucisses fumée (si, si!!), accompagné d\'un riz citronné</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 19: tout est permis, avec un burger végétarien ou à l\'effiloché de porc des alpilles, ses pommes de terre, son coleslow et sa salade!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les desserts de la semaine: il y aura un blanc-manger à la verveine et aux fruits de saison (abricots, fraises, par exemple, nous verrons bien), et probablement quelque chose d\'une tarte au chocolat, avec de la menthe, mais je ne sais pas encore quels jours verront ces douceurs</p>\n<!-- /wp:paragraph -->','Semaine du 15 au 19 juin','','publish','closed','open','','semaine-du-15-au-19-juin','','','2021-04-28 12:26:49','2021-04-28 12:26:49','',0,'https://lherbefolle.net/?p=454',0,'post','',0);
INSERT INTO `wp_posts` VALUES (457,6,'2020-06-21 13:09:29','2020-06-21 11:09:29','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>On retisse du lien, on renoue, on resociabilise, on dédistancialise... à l\'Herbe Folle, on maintient, on resserre.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et pour cette semaine de plein déconfinement scolaire, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 22: On commence la semaine en Italie, avec des pâtes à la Norma et une salade de saison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 23: Jour de marché </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 24: On simplifie, on zenifie, avec un bol de riz à la japonaise, accompagné de tofu, de légumes vapeur ou marinés </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 25: On retrouve le rougail, végétarien ou avec saucisses fumées maison et aubergines grillées</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 26: Jour Thaï par excellence. J\'hésite encore entre la salade aux nouilles et un curry rouge... </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les desserts de la semaine: Comme d\'habitude, cela variera en fonction de l\'humeur. Abricots, chocolat, c\'est sûr, crème fouettée peut-être, biscuit aux amandes...</p>\n<!-- /wp:paragraph -->','Semaine du 22 au 26 juin','','publish','closed','open','','semaine-du-22-au-26-juin','','','2021-04-28 12:27:06','2021-04-28 12:27:06','',0,'https://lherbefolle.net/?p=457',0,'post','',0);
INSERT INTO `wp_posts` VALUES (462,6,'2020-06-27 18:33:35','2020-06-27 16:33:35','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Le jardin continue à s\'ouvrir! Les places restent limitées. Nous sommes complets sur place lundi et jeudi, alors pensez à réserver votre table au plus tôt pour les autres jours!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lundi 29: Soupe froide de carottes au cumin, pain pita, salade de pois-chiche/herbes/parmesan/abricots </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 30: Jour de marché! Légumes encore plus frais que frais (malgré la chaleur), en crudités, grillés, poêlés, quelques céréales, des fruits, des graines...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 1er: Soupe froide de courgettes, pizzetta légumes, herbes et scarmozza fumée ou feta, petite salade de saison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 2: Salade Thaï végétarienne (ou pas, selon les goûts et l\'humeur)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>vendredi 3: Encore une petite hésitation entre l\'Orient et l\'Occident, mais les distances sont toutes relatives entre la coriandre et le basilic, alors on va probablement ouvrir les frontières et mélanger les cultures. Mise à jour Dimanche</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: on voguera probablement de soupes de fruits à tartelettes, en passant par un entremet aux parfums de saison. Ou pas.</p>\n<!-- /wp:paragraph -->','Semaine du 29 juin au 3 juillet','','publish','closed','open','','semaine-du-29-mai-au-3-juin','','','2021-04-28 12:27:54','2021-04-28 12:27:54','',0,'https://lherbefolle.net/?p=462',0,'post','',0);
INSERT INTO `wp_posts` VALUES (473,6,'2020-07-06 12:19:44','2020-07-06 10:19:44','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Première semaine de vacances, après un dernier trimestre des plus courts... Aussi, L\'Herbe Folle ne sera pas ouvert lundi 6.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>L\'Herbe Folle vous propose, pour lutter contre les grosses chaleurs:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi 7: Retour de marché!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 8: Gaspacho, pizzetta (maison) et salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 9: Croquettes de poisson aux herbes, salade, pain pita maison</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 10: Soupe froide de carottes et lentilles corail, aubergines grillées, tzaziki, pain maison   </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts de la semaine: Sûrement un overnight oat amandes/abricots/pêches; une tartelette fine aux fruits de saison et yaourt battu au miel</p>\n<!-- /wp:paragraph -->','Semaine du 6 au 10 juillet','','publish','closed','open','','semaine-du-6-au-10-juillet','','','2021-04-28 12:28:30','2021-04-28 12:28:30','',0,'https://lherbefolle.net/?p=473',0,'post','',0);
INSERT INTO `wp_posts` VALUES (476,6,'2020-07-10 13:05:43','2020-07-10 11:05:43','<!-- wp:paragraph -->\n<p>L\'Herbe Folle se met en jachère le temps des grosses chaleurs estivales. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Réouverture du jardin et du salon en septembre, pour de bons petits plats sains et de saison.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>D\'ici là, prenez soin de vous et de vos proches, profitez de cette pause - cette fois-ci volontaire - pour vous ressourcer, et revenez vite partager avec nous votre énergie !</p>\n<!-- /wp:paragraph -->','Pause estivale à l\'Herbe Folle','','publish','closed','open','','pause-estivale-a-lherbe-folle','','','2021-04-28 12:28:56','2021-04-28 12:28:56','',0,'https://lherbefolle.net/?p=476',0,'post','',0);
INSERT INTO `wp_posts` VALUES (484,6,'2020-08-27 16:30:44','2020-08-27 14:30:44','<!-- wp:paragraph -->\n<p>La rentrée a un petit quelque chose de progressif</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>C\'est avec un grand plaisir que le jardin et les salons de l\'Herbe Folle vous attendront dès le 1er septembre matin, avec de bons cafés, thés et infusions biologiques. Quelques douceurs aussi, probablement, ne boudons pas notre plaisir.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Le restaurant ne rouvrira pas avant le mois d\'octobre, mais, le 14 septembre signe le retour aux fourneaux et la vente à emporter qui reprend du service. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Des plats toujours aussi savoureux (mais comment pourraient-ils ne pas l\'être avec les délicieux fruits et légumes de Ludivine et Jérôme, nos maraîchers de Cucuron?), sur réservation la veille.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>le 14 septembre, c\'est aussi une proposition de goûters pour les enfants, pour accompagner leur sortie de l\'école, sainement mais avec gourmandise. L\'Herbe Folle s\'approprie là une tradition Bretonne qui lui est chère: la crêpe!!!!! avec du bon miel du Vaucluse, des confitures maisons, des fruits frais...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Alors n\'hésitez pas à ouvrir le portillon, vous poser un moment au vert en pleine ville et prendre le temps comme il vient.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A bientôt!</p>\n<!-- /wp:paragraph -->','La rentrée à l\'Herbe Folle','','publish','closed','open','','la-rentree-a-lherbe-folle','','','2021-04-28 12:30:01','2021-04-28 12:30:01','',0,'https://lherbefolle.net/?p=484',0,'post','',0);
INSERT INTO `wp_posts` VALUES (497,6,'2020-09-21 18:22:25','2020-09-21 16:22:25','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>C\'est une rentrée destructurée à l\'Herbe Folle.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Dans les cuisines de l\'Herbe Folle, ça devait mitonner dès le 14 mais le principe de précaution nous a amenés à reporter à la semaine du 21.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et les fourneaux seront à vif, du mercredi au vendredi, pour de bons plats à déguster chez soi.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi : On prend le principe de la Tajine et on l\'adapte à la cuisine Ibérique (dans une interprétation libre d\'une recette de Yotam Ottolenghi, un de nos chefs préférés): ça donne un délicieux <span style=\"color:#122c3a\" class=\"has-inline-color\">poulet Marbella</span>, aigre-doux aux figues et aux olives. Accompagné d\'un couscous vapeur au crucuma et de légumes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi : L\'Asie à nos assiettes. Un tofu mariné à la citronnelle et au soja, des nouilles de riz aux légumes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Histoire de finir la semaine en beauté, mais fonction des arrivages, on s\'oriente sur un poisson à chair blanche, et une sauce vierge</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les desserts de la semaine seront fruités et plus ou moins gourmands en fonction des jours, dans un petit jeu d\'équilibre avec les plats.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>N\'oubliez pas de m\'appeler la veille pour commander. A très vite!</p>\n<!-- /wp:paragraph -->','Semaine du 21 au 25 septembre','','publish','closed','open','','semaine-du-21-au-25-septembre','','','2021-04-28 12:35:12','2021-04-28 12:35:12','',0,'https://lherbefolle.net/?p=497',0,'post','',0);
INSERT INTO `wp_posts` VALUES (501,6,'2020-09-28 09:31:21','2020-09-28 07:31:21','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En cette froide dernière semaine de septembre, l\'Herbe Folle vous propose à emporter ...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: une bonne soupe thaï de lentilles corail, rien de mieux en milieu de semaine. On l\'accompagnera d\'une salade verte et de pain maison </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Ca nous a bien plu la semaine dernière, alors on refait un poulet Marbella!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: On fait léger pour pouvoir s\'autoriser un weekend ..., avec une salade mixte, légumes crus et grillés, petites burrata, une sauce salée-sucrée-acide, des herbes aromatiques, et un bon pain maison au curry/curcuma. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et les desserts de la semaine seront, comme d\'habitude, fonction de l\'humeur... mais c\'est la saison des pommes et des poires, il reste peut-être quelques figues, des prunes... on devrait bien s\'entendre.</p>\n<!-- /wp:paragraph -->','Semaine du 28 au 1er octobre','','publish','closed','open','','semaine-du-28-au-1er-octobre','','','2021-04-28 12:30:49','2021-04-28 12:30:49','',0,'https://lherbefolle.net/?p=501',0,'post','',0);
INSERT INTO `wp_posts` VALUES (530,6,'2020-10-05 19:16:24','2020-10-05 17:16:24','<!-- wp:paragraph -->\n<p>Alors cette semaine, l\'Herbe Folle se paye le luxe d\'un petit détournement japonais, avec:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: une Blanquette de veau revisitée (bouillon dashi maison, kombu confit, gingembre...)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: une Salade Caesar façon Empire du Levant</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Ceci n\'est pas une Parmigiana d\'aubergines</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et toujours des desserts improvisés en fonction de l\'envie du moment</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Semaine du 5 au 9 octobre','','publish','closed','open','','semaine-du-5-au-9-octobre','','','2021-04-28 12:32:30','2021-04-28 12:32:30','',0,'https://lherbefolle.net/?p=530',0,'post','',0);
INSERT INTO `wp_posts` VALUES (537,6,'2020-10-12 15:57:45','2020-10-12 13:57:45','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Tourte aux lentilles et patates douces, accompagnée d\'une belle salade / mozzarella/oranges</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Effiloché de porc façon carbonade, pommes de terre rôties, salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Curry Thaï au poulet et patates douces </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour le dessert, le suspens reste de mise, comme d\'habitude!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>les viandes sont d\'origine française, le porc du Sud-Ouest Label Rouge, le poulet d\'origine biologique. Les fruits et légumes locaux, en agriculture raisonnée, ou biologique. A l\'exception du lait de coco et des épices utilisées.</p>\n<!-- /wp:paragraph -->','Semaine du 12 au 16 octobre','','publish','closed','open','','semaine-du-12-au-16-octobre','','','2021-04-28 12:33:47','2021-04-28 12:33:47','',0,'https://lherbefolle.net/?p=537',0,'post','',0);
INSERT INTO `wp_posts` VALUES (541,6,'2020-10-19 20:37:33','2020-10-19 18:37:33','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>L\'Herbe Folle vous propose, cette semaine:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Poulet croustillant, légumes grillés, pain maison aux herbes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Courge Little Jack farcie et salade, avec un petit pain maison au curcuma et curry</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Soupe de courge et lentilles corail, ravioles maison aux herbes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour le dessert, on reste sur de la pomme, de la poire, et des scoubidoubidou</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Le poulet, Label Rouge, est d\'origine française, de Camargue plus exactement, </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Dans les allergies de la semaine, on retrouve du gluten, du sésame, des oeufs (pour les desserts). Mais on peut modifier tout ça en fonction de vous, alors n\'hésitez pas à appeler.</p>\n<!-- /wp:paragraph -->','Semaine du 19 au 23 octobre','','publish','closed','open','','semaine-du-19-au-23-octobre','','','2021-04-28 12:35:42','2021-04-28 12:35:42','',0,'https://lherbefolle.net/?p=541',0,'post','',0);
INSERT INTO `wp_posts` VALUES (546,6,'2020-10-25 10:45:03','2020-10-25 09:45:03','<!-- wp:paragraph -->\n<p>bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe&nbsp;Folle vous propose&nbsp;:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi&nbsp;: Courge Little jack farcie et une bonne salade aux olives, tomates cerises, fenouil, graines et quinoa</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: intermède indien avec un poulet tandoori, curry de légumes et naan aux herbes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: LA tourte lentilles et patates douce. Accompagnée de salade, bien entendu.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En dessert, après la semaine des choux, ce sera probablement la semaine du cheesecake léger. Aux poires, au citron, on variera les plaisirs...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> </p>\n<!-- /wp:paragraph -->','Semaine du 26 au 30 octobre','','publish','closed','open','','semaine-du-26-au-30-octobre','','','2021-04-28 12:38:16','2021-04-28 12:38:16','',0,'https://lherbefolle.net/?p=546',0,'post','',0);
INSERT INTO `wp_posts` VALUES (551,6,'2020-11-01 21:03:13','2020-11-01 20:03:13','<!-- wp:paragraph -->\n<p>Cette première semaine d\'un mois qui pourrait s\'annoncer morose si je ne nous savais pas posséder autant de ressources pour inverser la tendance et nous enrichir les uns les autres, L\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: salade de légumes grillés et ravioles, herbes et tomates confites. Pain au curcuma</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: kebabs de poisson, sauce au yaourt et aux herbes, salade et pain pita </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: soupe thaï de lentilles corail, huile épicée, pain vapeur  aux herbes et à la viande ou vegan </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les desserts seront au chocolat, aux poires et aux pommes. Avec une pointe de crème fouettée, probablement.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Est-il besoin de le préciser, mais tout est fait maison, à partir des delicieux fruits et legumes de Ludivine et Jérôme, NOS maraîchers de Cucurron (mais on veut bien partager), ou pour certains produits (lentilles, agrumes,...), de producteurs en agriculture biologique, locaux si possible. Les herbes sont du jardin, les viandes de petits producteurs locaux (le boeuf et le porc sont de tarascon). Les oeufs, la cremerie sont issus de l\'agriculture biologique et sont en circuit court.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Semaine du 2 au 6 novembre','','publish','closed','open','','semaine-du-2-au-6-novembre','','','2021-04-28 12:38:38','2021-04-28 12:38:38','',0,'https://lherbefolle.net/?p=551',0,'post','',0);
INSERT INTO `wp_posts` VALUES (554,6,'2021-04-24 13:15:06','2021-04-24 13:15:06','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Comme un tajine de poulet aux coings</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: </p>\n<!-- /wp:paragraph -->','Semaine du 9 au 13 novembre','','trash','closed','open','','__trashed-2','','','2021-04-24 13:15:06','2021-04-24 13:15:06','',0,'https://lherbefolle.net/?p=554',0,'post','',0);
INSERT INTO `wp_posts` VALUES (555,6,'2020-11-08 21:02:16','2020-11-08 20:02:16','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Après l\'ondée salvatrice de ce weekend, et malgré des températures un peu sens-dessus-dessous, l\'Herbe Folle a des papilles automnales et vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Comme un Koresht de poulet aux coings, amandes grillées. Le Koresht est une recette persanne de viande, servie avec du riz. Un régal sucré-salé-acide. Et là, j\'ai des coings d\'un petit jardin familial des Alpes, alors pourquoi s\'en priver?  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Comme un bol de riz japonais... à la courge grillée, aux shiitakes, aux blettes marinées</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Comme des croquettes de cabillaud à la tomate façon Yotam Ottolenghi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour le dessert, je ne sais pas encore. Je suis retombé dans ma phase citron, alors il y aura sûrement de l\'acidulé, probablement vendredi. Mais dans tous les cas on reste avec les fruits de saison, pommes, poires, coings (des kakis aussi - je garde jalousement pour moi ceux de mes si chers amis de la fraternité - mais je suis au désespoir devant le peu de personnes qui aiment ce délicat délice). Je pense qu\'il y aura aussi un cheesecake, c\'est bon, ça, le cheesecake.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les allergies, il y a du gingembre, des fruits à coques le mercredi, du soja, des sésames le jeudi, du gluten, du gingembre, des oeufs et du poisson le vendredi. Les desserts ont souvent des produits laitiers, de la farine de blé, des oeufs... Mais prévenez-moi, je peux toujours adapter les plats.</p>\n<!-- /wp:paragraph -->','Semaine du 9 au 13 novembre','','publish','closed','open','','semaine-du-9-au-13-novembre','','','2021-04-28 12:43:23','2021-04-28 12:43:23','',0,'https://lherbefolle.net/?p=555',0,'post','',0);
INSERT INTO `wp_posts` VALUES (565,6,'2020-11-15 20:06:50','2020-11-15 19:06:50','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Après une semaine de nouveaux essais culinaires, l\'Herbe Folle vous propose de revenir à ses incontournables, avec:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Oyakudon, ce bol de riz poulet/oeuf si délicat et parfumé. Avec des légumes grillés et marinés.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Salade de légumes grillés, burrata ou mozzarella de chez Castellano, pain maison aux herbes et aux olives.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Elle revient, avec des mangues bio d\'Espagne et une adaptation de saison pour les légumes... La salade thaï de l\'Herbe&nbsp;Folle! Aux crevettes biologiques ou végétarienne</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et en dessert, un entremet au thé vert et aux poires et des tartes fines de saison. Peut être du chocolat, si le temps se met au gris.</p>\n<!-- /wp:paragraph -->','Semaine du 16 au 20 novembre 2020','','publish','closed','open','','semaine-du-16-au-20-novembre-2020','','','2021-04-28 12:44:46','2021-04-28 12:44:46','',0,'https://lherbefolle.net/?p=565',0,'post','',0);
INSERT INTO `wp_posts` VALUES (569,6,'2021-01-05 14:01:49','2021-01-05 13:01:49','<!-- wp:paragraph -->\n<p>Ne boudons pas notre plaisir, Le dimanche, Je brunche, tu brunches, elle-il-on brunche, nous brunchons, vous brunchez, ils brunchent</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Au menu ! </strong> </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Du frais, du sain, du savoureux, la rencontre de goûts simples et de petits plats recherchés. Des produits de saison, les fruits et légumes de Ludivine et Jérôme, nos maraîchers, le pain biologique de chez Noé, et tant d\'autres produits issus d\'une agriculture locale, biologique ou raisonnée. Le menu est toujours élaboré et réalisé en collaboration avec notre aventurière amie. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et comme cela vous a plu, on reste sur notre proposition des deux premiers brunchs, avec au menu: des pancakes moelleux et des tartinades, un tendre et gourmand carrot cake, un yaourt végétal et granola (les deux faits maison, bien entendu), la délicieuse tartine truite fumée bio du moulin de Caoulet/ricotta de chez Castellano/avocat, des oeufs brouillés et bacon&nbsp;bio, du pain frais. Et une boisson végétale maison pour accompagner le tout (on vous laisse le plaisir de l\'odeur du café frais)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les enfants, le menu est le même mais les portions adaptées.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Informez-nous de vos besoins, intolérances ou allergies, nous modifierons le menu pour qu\'il vous corresponde.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et comme on est enfin en 2021, les tarifs sont de 18 euros pour les adultes, 10 euros pour les enfants.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les brunchs sont à récupérer dans notre jardin entre 10h et 12h30</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Réservez vite - et au plus tard samedi midi - votre coupable moment de plaisir dominical, en appelant au 06.25.33.49.13</p>\n<!-- /wp:paragraph -->','Dimanche 17 Janvier','','publish','closed','open','','je-brunche-tu-brunches-elle-il-on-brunche-nous-brunchons-ils-brunchent','','','2021-04-28 12:52:55','2021-04-28 12:52:55','',0,'https://lherbefolle.net/?p=569',0,'post','',0);
INSERT INTO `wp_posts` VALUES (577,6,'2020-11-22 20:58:01','2020-11-22 19:58:01','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En cette fraîche semaine, l\'Herbe Folle va faire dans le réconfortant et vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Epaule d\'agneau confite à la menthe et au cumin. Semoule aux dattes. On descend un peu chercher la chaleur du Sud.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Butternut rôtie aux lentilles et à la feta bio IGP, riz au citron. Du tendre, de l\'automnal, du crémeux.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Tarte aux coings et au bleu ou Pithiviers aux champignons. Avec une belle salade et des carottes rôties à la cannelle.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les desserts, qui sait? Du doux, de saison, c\'est certain. J\'affinerai en fonction du marché mais j\'y vois bien de la noix, des poires et une pointe d\'agrumes.</p>\n<!-- /wp:paragraph -->','Semaine du 23 au 27 novembre','','publish','closed','open','','semaine-du-23-au-27-novembre','','','2021-04-28 12:45:46','2021-04-28 12:45:46','',0,'https://lherbefolle.net/?p=577',0,'post','',0);
INSERT INTO `wp_posts` VALUES (617,6,'2020-11-29 20:36:06','2020-11-29 19:36:06','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Décembre arrive enfin, les mois furent éprouvants, mais les initiatives de chacun autour de nous, la bonne humeur de nos voisins et amis, les petites attentions de chacun, nous ont permis de traverser cette période en vrais privilégiés. Mille mercis!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>L\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Un peu de la simplicité du Japon pour commencer, avec un bol de riz, blettes au gingembre, carottes rôties, poulet mariné</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: le sucré-salé-crémeux-piquant de la tarte coings/courge/gorgonzola et une salade fraîche. Du bonheur </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: La chaleur de la soupe thaï et son pain épicé à la viande ou végétarien. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les desserts, tartes fines, fromage blanc fouetté et châtaignes, seront de la partie.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!&nbsp;</p>\n<!-- /wp:paragraph -->','Semaine du 30 au 4 décembre','','publish','closed','open','','semaine-du-30-au-4-decembre','','','2021-04-28 12:46:12','2021-04-28 12:46:12','',0,'https://lherbefolle.net/?p=617',0,'post','',0);
INSERT INTO `wp_posts` VALUES (630,6,'2020-12-06 21:04:28','2020-12-06 20:04:28','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe&nbsp;Folle&nbsp;vous&nbsp;propose&nbsp;:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi : Une soupe de nouilles japonaises au boeuf ou au tofu. Un bol revigorant, plein de saveurs, le sel de la bonite séchée traditionnelle, le fondant du boeuf juste grillé, le moelleux d\'un oeuf mollet, la chaleur du gingembre, le croquant des légumes et les nouilles fraîches.... </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Une soupe de courge Butternut à l\'huile de noisette et des raviolis au fromage de chèvre d\'un petit fabricant des Alpilles. Que dire de plus? Un plaisir simple et réconfortant qu\'accompagne une salade fraîche, des graines, quelques amandes grillées</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Un poisson aux épices façon japonaise, cuit en basse température, sur un bol de riz parfumé, des légumes sautés craquants - peut être des tempura de citron </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En dessert, des tartes fines pommes/citron, un entremet au&nbsp;thé&nbsp;vert, quelque poêlée de poires et pommes, très certainement...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!</p>\n<!-- /wp:paragraph -->','Semaine du 7 au 11 décembre','','publish','closed','open','','semaine-du-7-au-11-decembre','','','2021-04-28 12:47:39','2021-04-28 12:47:39','',0,'https://lherbefolle.net/?p=630',0,'post','',0);
INSERT INTO `wp_posts` VALUES (636,6,'2020-12-13 20:10:34','2020-12-13 19:10:34','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Dernière semaine herbefollesque avant les vacances. Nous éteignons le piano pour deux semaines et réouvrirons nos fourneaux le 6 janvier.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>l\'Herbe&nbsp;Folle vous propose cette semaine:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Tajine de&nbsp;poulet aux agrumes et dattes. un petit parfum de safran, des légumes fondants, une viande juteuse et le sucré/acide des agrumes et des dattes </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Risotto aux champignons. Un risotto crémeux et parfumé, accompagné d\'une salade verte rafraîchissante aux graines et à l\'huile d\'olives du moulin d\'Eguilles (avec la participation de notre olivier!)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Lentilles au gingembre et potimarron rôti, saucisse de Morteau (porc français label rouge) et/ou gorgonzola. Pain maison aux herbes (et peut-être cuit vapeur, qui sait)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En dessert, se bousculeront joyeusement une crème brûlée à la fleur d\'oranger, une tarte au citron, et quelques craquants morceaux de poire </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!</p>\n<!-- /wp:paragraph -->','Semaine du 14 au 18 décembre','','publish','closed','open','','semaine-du-14-au-18-decembre','','','2021-04-28 12:49:18','2021-04-28 12:49:18','',0,'https://lherbefolle.net/?p=636',0,'post','',0);
INSERT INTO `wp_posts` VALUES (648,1,'2020-12-16 20:59:43','2020-12-16 19:59:43','<!-- wp:paragraph -->\n<p>Bienvenue à l\'herbe Folle, une cantine bio au coeur d\'Aix en Provence</p>\n<!-- /wp:paragraph -->','ACCUEIL','','publish','closed','closed','','accueil','','','2020-12-16 20:59:43','2020-12-16 19:59:43','',0,'https://lherbefolle.net/?page_id=648',0,'page','',0);
INSERT INTO `wp_posts` VALUES (650,6,'2020-12-19 18:04:07','2020-12-19 17:04:07','<!-- wp:paragraph -->\n<p>Chers amis,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>L\'Herbe Folle se met au vert le temps des vacances de Noël! Et éteint son fourneau du 19 décembre au 4 janvier. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Carole, Candice et les poulettes (et Flipette le chat, de loin...) se joignent à moi pour vous souhaiter de réjouissantes fêtes, quelques réparatrices retrouvailles familiales et amicales malgré les contraintes actuelles (l\'occasion d\'être imaginatif pour concilier distanciation et célébration!), et moult plaisirs gustatifs. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et un bon bout d\'an aussi, tiens. C\'est étrange de l\'attendre avec autant d\'impatience - 2020 ayant été une année mémorable que l\'on aimerait rapidement oublier - tout en redoutant, quand même, l\'arrivée de 2021 et son lot d\'incertitudes...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>N\'oublions pas que nous avons chacun une part de responsabilité dans nos (in)actions, et surtout une part des réponses aux défis qui se présentent. Profitons, justement, de cette fin d\'année pour prendre le temps de penser aux décisions à prendre, aux actes à poser, individuellement et ensemble, pour faire de l\'année qui vient un vrai renouveau...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A l\'an qué ven, donc, les amis, si sian pas maï, que sian pas men.</p>\n<!-- /wp:paragraph -->','Jingle bells, jingle bells, rock!','','publish','closed','open','','jingle-bells-jingle-bells-rock','','','2021-04-28 12:50:24','2021-04-28 12:50:24','',0,'https://lherbefolle.net/?p=650',0,'post','',0);
INSERT INTO `wp_posts` VALUES (671,6,'2021-01-03 17:33:38','2021-01-03 16:33:38','<!-- wp:paragraph -->\n<p>Aaaaah, elle est là, la fameuse nouvelle année, celle qui change tout, qui bouleverse l\'ordre établi et entre dans l\'humanisme éclairé. Celle aussi qui nous voit devenir adultes et parents responsables et faire les sacrifices de confort nécessaires pour protéger notre planète et la transmettre à nos enfants.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Bonne année, donc, que vous soyez doux avec elle, elle saura vous le rendre au centuple.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Et pour commencer l\'année, l\'Herbe&nbsp;Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Un bon Seco de pollo vert. Plat d\'Amérique latine, une recette du Pérou que m\'a faite découvrir une amie. Un poulet confit à la coriandre et à la bière&nbsp;(brassée maison!!!). Un vrai régal!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Une chaude soupe de lentilles corail accompagnée d\'un bun aux épices et aux oignons confits. Que dire de plus?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Un poisson MSC pêché à la ligne (je ne sais pas encore ce que le poissonnier pourra me proposer), mariné au soja et au Mirin, accompagné d\'algues, légèrement relevé de gingembre et rôti au four. Peut être des tempura, du riz sûrement, quelques légumes&nbsp;rotis au miso. Un régal pour les papilles et un bonheur pour l\'estomac.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Des desserts légers, légers, aux fruits, pour ne pas rajouter trop de sucres aux festivités de fin d\'année et à la galette de ce dimanche...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite, profitez bien de ce dimanche</p>\n<!-- /wp:paragraph -->','Une belle année...','','publish','closed','open','','671-2','','','2021-04-28 12:51:23','2021-04-28 12:51:23','',0,'https://lherbefolle.net/?p=671',0,'post','',0);
INSERT INTO `wp_posts` VALUES (683,6,'2021-01-10 15:49:02','2021-01-10 14:49:02','<!-- wp:paragraph -->\n<p>Cette&nbsp;semaine, l\'Herbe&nbsp;Folle&nbsp;remet le couvert le mardi pour réchauffer un peu notre quotidien bien refroidi et vous&nbsp;propose&nbsp;:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi: Un velouté de butternut aux chanterelle et au gorgonzola, accompagné d\'une pizzetta aux oignons et d\'une petite salade aux graines;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Un rougail à la saucisse de Morteau (D\'un petit producteur de porc Label Rouge), accompagné de riz et&nbsp;de légumes rôtis</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: pas d\'Herbe Folle ce jeudi, jour tant attendu de réparation et remise en route de chaudière! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Le poisson du jour (la semaine dernière, c\'était un saumon sauvage de ligne, qui sait ce que nous réserve cette semaine...), mariné et grillé au sésame, avec un écrasé de pommes de terre au wasabi. Accompagné d\'une salade de chou au shoyu.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les desserts, dans le désordre, il y aura très certainement un éclair au citron accompagné d\'une compotée de pommes, un fromage fouetté sur une gelée au thé Earl grey.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite, bonne fin (anticipée) de weekend</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Semaine du 11 au 15 janvier','','publish','closed','open','','semaine-du-11-au-15-janvier','','','2021-04-28 12:55:01','2021-04-28 12:55:01','',0,'https://lherbefolle.net/?p=683',0,'post','',0);
INSERT INTO `wp_posts` VALUES (692,6,'2021-01-17 21:52:00','2021-01-17 20:52:00','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, L\'Herbe&nbsp;Folle vous propose un détour par l\'Afrique, avec:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Mardi: c\'est retour de marché! Alors on va faire du végétarien un peu comme en Éthiopie. Des légumes épicés, des épinards frais et du fromage frais. Et une crêpe pour accompagner le tout.</li><li>Mercredi: Comme un Yassa au poulet. Un délicieux plat à base d\'oignons, de&nbsp;viande marinée dans du citron, et d\'épices. </li><li>Jeudi: Comme un Mafe de patates douces et carottes. Une version végétarienne de ce délice de sauce aux arachides</li><li>Vendredi: Comme un Thieboudienne. Un autre grand plat traditionnel d\'Afrique de l\'ouest, à base de riz, de poisson, une lente cuisson dans une sauce dont chaque famille a le secret. Alors ce sera façon Herbe Folle...</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>Pour les desserts, on aura certainement un flan à la noix de coco (bio), un Baba au/sans rhum (je ne m\'en lasse pas...)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très&nbsp;vite!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Semaine du 18 au 22 janvier','','publish','closed','open','','semaine-du-18-au-22-janvier','','','2021-04-28 12:55:47','2021-04-28 12:55:47','',0,'https://lherbefolle.net/?p=692',0,'post','',0);
INSERT INTO `wp_posts` VALUES (700,6,'2021-01-24 21:13:07','2021-01-24 20:13:07','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Une furieuse envie de Japon secoue L\'Herbe&nbsp;Folle cette semaine. Et comme il est bon de s\'écouter, en cuisine, je vous propose une semaine donburi, le fameux bol de riz garni!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Mercredi: c\'est le jour des enfants, soyons un peu régressifs.... Avec un hambagu, LE steak à burger japonais. Délicieux mélange de boeuf et de porc hachés&nbsp;grillé, déglacé au vin rouge (ou pas), une petite sauce tonkatsu maison. Sur un bol de doux riz japonais, accompagné de petits légumes juste revenus et d\'une salade de chou et sésames. Ou, comme tout est permis, une version vraiment régressive, avec un moelleux pain brioché et des frites de patates douces est envisagé, avis aux amateurs!</li><li>Jeudi: Le jeudi, c\'est végétarien à L\'Herbe Folle, on se régale d\'un Ten don, un donburi aux tempuras!</li><li>Vendredi: Un donburi au saumon mi-cuit, façon teriyaki. Un saumon sauvage ASC, pêché à la ligne, grillé à l\'unilatérale et&nbsp;glacé... Que dire de plus...</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>Question desserts, il y aura des choux au thé vert et à la vanille, peut-être même au citron, tiens. Et comme une panna cotta au thé matcha, avec des poires caramélisées et une touche de compote de prunes du jardin.</p>\n<!-- /wp:paragraph -->','Semaine du ...ah, on me dit qu\'il y a un petit problème technique...','','publish','closed','open','','semaine-du-25-au-29-janvier','','','2021-04-28 12:56:17','2021-04-28 12:56:17','',0,'https://lherbefolle.net/?p=700',0,'post','',0);
INSERT INTO `wp_posts` VALUES (714,6,'2021-01-26 09:21:50','2021-01-26 08:21:50','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour quelques temps encore, L\'Herbe Folle garde éteints ses fourneaux et reste en suspension... Le \"petit accident\" est un peu plus grave que prévu et je n\'ai pas eu l\'autorisation de me remettre en cuisine.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Hors - peut-être - quelques évènements auxquels vous serez conviés, petits temps culinaires impromptus et d\'exception, il nous faudra attendre encore quelques semaines avant de pouvoir nous retrouver. Mais retrouvailles il y aura!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Au plaisir de vous revoir, prenez soin de vous.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Denis </p>\n<!-- /wp:paragraph -->','Interlude','','publish','closed','open','','interlude','','','2021-04-28 12:57:02','2021-04-28 12:57:02','',0,'https://lherbefolle.net/?p=714',0,'post','',0);
INSERT INTO `wp_posts` VALUES (727,6,'2021-03-05 15:32:12','2021-03-05 14:32:12','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Il en aura fallu du temps, mais l\'Herbe Folle rouvre enfin, pour vous régaler à nouveau de ses plats cuisinés avec passion.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, pour la rentrée, je vous propose une reprise de la semaine prévue initialement, autour du Japon et de ses donburi, fameux bols de riz:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mardi: C\'est retour de marché, et il y aura du végétal dans vos assiettes! Des blettes poêlées au soja et au gingembre, des carottes juste revenues, quelques doux morceaux de navets à l\'huile de sésame.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Hambagu, LE steak à burger japonais. Délicieux mélange de bœuf et de porc haché,&nbsp;grillé, déglacé au vin rouge (ou pas), servie avec une petite sauce tonkatsu maison. Sur un bol de doux riz japonais, accompagné de petits légumes juste revenus et d\'une salade de chou et sésames. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Le jeudi, c\'est végétarien à L\'Herbe Folle, on se régale d\'un Ten don!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Un donburi au saumon mi-cuit, façon teriyaki. Un saumon sauvage ASC, pêché à la ligne, grillé à l\'unilatérale et&nbsp;glacé... Que dire de plus...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les desserts, je pense à des choux à la vanille et au citron, quelques poires et pommes pochées et une petite chantilly</p>\n<!-- /wp:paragraph -->','Fin de l\'interlude - semaine du 8 au 12 mars','','publish','closed','open','','fin-de-linterlude-semaine-du-8-au-12-mars','','','2021-04-28 12:58:04','2021-04-28 12:58:04','',0,'https://lherbefolle.net/?p=727',0,'post','',0);
INSERT INTO `wp_posts` VALUES (741,6,'2021-03-14 23:17:07','2021-03-14 22:17:07','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe Folle repart sur des sentiers culinaires décalés, avec:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Mardi: c\'est retour de marché! Les premières asperges devraient être de la partie, accompagnées de quelques improvisations du moment et de craquant pain maison aux herbes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Mercredi: un saumon gravad lax&nbsp;et sa sauce salée-sucrée croisera l\'acide douceur d\'une salade russe de betteraves et pommes et la tendresse de quelques Pompadour</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Jeudi: On suivra le chef Ottolenghi avec un guacamole revisité et des carottes à la cannelle, accompagnant une burratta crémeuse à souhait et une ciabatta maison. Arrosées d\'huile d\'olives de la Fraternité.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Vendredi: De délicieuses bouchées d\'agneau aux pistaches, du yaourt au zaatar et une salade de roquette au parmesan, du pain maison, clôtureront cette semaine aventureuse.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les desserts je ne sais trop pour le moment, mais j\'ai une petite envie de tarte au citron et de profiter des dernières saveurs hivernales. On verra bien, je mettrai à jour le menu dès que je me serai décidé...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!</p>\n<!-- /wp:paragraph -->','Semaine du 15 au 19 mars','','publish','closed','open','','semaine-du-15-au-19-mars','','','2021-04-28 12:58:28','2021-04-28 12:58:28','',0,'https://lherbefolle.net/?p=741',0,'post','',0);
INSERT INTO `wp_posts` VALUES (744,6,'2021-03-21 21:28:04','2021-03-21 20:28:04','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, L\'Herbe&nbsp;Folle s\'offre un voyage en Thaïlande et vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Mardi: c\'est retour de marché et des nouilles sautées&nbsp;aux légumes, aux saveurs délicates, régaleront vos papilles&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Mercredi: une salade tiède au boeuf, asperges et gingembre, saura combler les palais les plus délicats </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Jeudi: c\'est bon et réconfortant un pad thaï, c\'est idéal pour résister aux derniers frimats</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>. Vendredi: on termine la semaine&nbsp;avec un curry rouge crevettes et poisson,  une explosion d\'arômes et de parfums, apaisée par la douceur du lait de coco </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour les desserts, j\'espère trouver de la mangue bio d\'Espagne. Dans tous les cas, il y aura&nbsp;sûrement une tarte fine, une crème coco/caramel et un cake citron-pavot.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!</p>\n<!-- /wp:paragraph -->','Semaine du 22 au 26 mars','','publish','closed','open','','semaine-du-22-au-26-mars','','','2021-04-28 12:59:06','2021-04-28 12:59:06','',0,'https://lherbefolle.net/?p=744',0,'post','',0);
