INSERT INTO `wp_posts` VALUES (1093,1,'2021-04-28 11:50:34','2021-04-28 11:50:34','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Par solidarité avec une terre qui n\'en finit plus de s’échauffer, l\'Herbe Folle est montée en température elle aussi.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Elle ne pourra régaler vos papilles avant jeudi 4 juillet et vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi, c\'est poisson et orient: </strong>Kebab de cabillaud, sauce au yaourt et salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi, c\'est courgettes, Grèce et Italie: </strong>Parpadelles aux courgettes et Feta</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert du moment</strong>: Salade de fruits frais à la menthe, cake au citron</p>\n<!-- /wp:paragraph -->','Semaine du 1er au 5 juillet','kebab de poisson\nparpadelles aux courgettes et feta\ncake au citron et salade de fruits','inherit','closed','closed','','231-revision-v1','','','2021-04-28 11:50:34','2021-04-28 11:50:34','',231,'http://herbefollewordpress.local/?p=1093',0,'revision','',0);
