INSERT INTO `wp_posts` VALUES (1118,1,'2021-04-28 12:05:36','2021-04-28 12:05:36','<!-- wp:paragraph -->\n<p>Tous nos voeux pour cette nouvelle année! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Après la trêve des confiseurs, l\'Herbe Folle vous propose de nous retrouver dès le 2 janvier pour une pause déjeuner détoxifiante autour de bouillons et soupes de saison.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 2 janvier: Ramen aux crevettes ou au poulet - soupe de nouilles japonaise (bouillon dashi/bouillon poulet-soja et nouilles maison, crevettes biologiques/poulet biologique, épinards, carottes, courge...)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 3 janvier: Soupe au céleri et saumon mariné. Pain aux céréales, fromage frais</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts du jour: tarte fine aux clémentines ou fromage frais battu au miel de lavandes d\'Aurel et fruits de saison poêlés</p>\n<!-- /wp:paragraph -->','Réouverture de l\'Herbe Folle le 2 janvier','','inherit','closed','closed','','307-revision-v1','','','2021-04-28 12:05:36','2021-04-28 12:05:36','',307,'http://herbefollewordpress.local/?p=1118',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (1119,1,'2021-04-28 12:06:15','2021-04-28 12:06:15','<!-- wp:paragraph -->\n<p>Bonjour à tous,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour tenir cette semaine de rentrée tout en récupérant des vacances, l\'Herbe Folle vous propose des plats régénérants et purifiants.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi 08 janvier: Soupe de courge au lait de coco - curry au poulet et légumes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi 09 janvier: Soupe au céleri - bucatini aux noix/feta/courge rôtie/blettes poêlées</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi 10 janvier: Soupe miso - Donburi (bol de riz vinaigré au poulet et oeufs battus) et légumes marinés</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desserts du moment: Tarte fine aux oranges et compotée poires/pommes/ fromage grec battu au miel</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Flan au thé matcha et pommes/poires poêlées au miel (vendredi)</p>\n<!-- /wp:paragraph -->','Semaine du 06 au 10 janvier 2020','','inherit','closed','closed','','313-revision-v1','','','2021-04-28 12:06:15','2021-04-28 12:06:15','',313,'http://herbefollewordpress.local/?p=1119',0,'revision','',0);
