INSERT INTO `wp_posts` VALUES (1152,1,'2021-04-28 12:33:46','2021-04-28 12:33:46','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Tourte aux lentilles et patates douces, accompagnée d\'une belle salade / mozzarella/oranges</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Effiloché de porc façon carbonade, pommes de terre rôties, salade</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Curry Thaï au poulet et patates douces </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour le dessert, le suspens reste de mise, comme d\'habitude!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>les viandes sont d\'origine française, le porc du Sud-Ouest Label Rouge, le poulet d\'origine biologique. Les fruits et légumes locaux, en agriculture raisonnée, ou biologique. A l\'exception du lait de coco et des épices utilisées.</p>\n<!-- /wp:paragraph -->','Semaine du 12 au 16 octobre','','inherit','closed','closed','','537-revision-v1','','','2021-04-28 12:33:46','2021-04-28 12:33:46','',537,'http://herbefollewordpress.local/?p=1152',0,'revision','',0);
