INSERT INTO `wp_posts` VALUES (1154,1,'2021-04-28 12:35:41','2021-04-28 12:35:41','<!-- wp:paragraph -->\n<p>Bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>L\'Herbe Folle vous propose, cette semaine:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi: Poulet croustillant, légumes grillés, pain maison aux herbes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: Courge Little Jack farcie et salade, avec un petit pain maison au curcuma et curry</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: Soupe de courge et lentilles corail, ravioles maison aux herbes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour le dessert, on reste sur de la pomme, de la poire, et des scoubidoubidou</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Le poulet, Label Rouge, est d\'origine française, de Camargue plus exactement, </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Dans les allergies de la semaine, on retrouve du gluten, du sésame, des oeufs (pour les desserts). Mais on peut modifier tout ça en fonction de vous, alors n\'hésitez pas à appeler.</p>\n<!-- /wp:paragraph -->','Semaine du 19 au 23 octobre','','inherit','closed','closed','','541-revision-v1','','','2021-04-28 12:35:41','2021-04-28 12:35:41','',541,'http://herbefollewordpress.local/?p=1154',0,'revision','',0);
