INSERT INTO `wp_posts` VALUES (1157,1,'2021-04-28 12:38:16','2021-04-28 12:38:16','<!-- wp:paragraph -->\n<p>bonjour,</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, l\'Herbe&nbsp;Folle vous propose&nbsp;:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Mercredi&nbsp;: Courge Little jack farcie et une bonne salade aux olives, tomates cerises, fenouil, graines et quinoa</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Jeudi: intermède indien avec un poulet tandoori, curry de légumes et naan aux herbes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Vendredi: LA tourte lentilles et patates douce. Accompagnée de salade, bien entendu.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>En dessert, après la semaine des choux, ce sera probablement la semaine du cheesecake léger. Aux poires, au citron, on variera les plaisirs...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A très vite!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> </p>\n<!-- /wp:paragraph -->','Semaine du 26 au 30 octobre','','inherit','closed','closed','','546-revision-v1','','','2021-04-28 12:38:16','2021-04-28 12:38:16','',546,'http://herbefollewordpress.local/?p=1157',0,'revision','',0);
