INSERT INTO `wp_posts` VALUES (175,6,'2019-05-27 18:11:56','2019-05-27 16:11:56','<!-- wp:paragraph -->\n<p>Bonjour à tous</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour cette semaine du 27 au 31 mai 2019, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Lundi 27 mai: pâtes au citron et parmesan, légumes poêlés</li><li>Mardi 28 mai: Salade Thaïe au boeuf ou à la truite fumée - ou végétarienne</li><li>Mercredi 29 mai: Couscous° au poulet° ou végétarien, petite salade</li><li>Jeudi 30 mai: Férié - mais je me ferai un plaisir de régaler vos papilles si vous passez commande la veille! Nous pourrons même créer ensemble votre plat du jour</li><li>Vendredi 31 mai: Tartine de légumes* grillés et feta, velouté de carottes* au cumin° et petite salade*</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>° produits issus de l\'agriculture biologique</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>produits issus d\'une agriculture locale et raisonnée</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:gallery {\"ids\":[178,180,182,183],\"columns\":4,\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-4 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/05/P5200994-e1558972730835-576x1024.jpg\" alt=\"\" data-id=\"178\" data-link=\"http://lherbefolle.net/?attachment_id=178\" class=\"wp-image-178\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/05/20190507_120734-1024x576.jpg\" alt=\"\" data-id=\"180\" data-link=\"http://lherbefolle.net/?attachment_id=180\" class=\"wp-image-180\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/05/P4250627-1024x576.jpg\" alt=\"\" data-id=\"182\" data-link=\"http://lherbefolle.net/?attachment_id=182\" class=\"wp-image-182\"/></figure></li><li class=\"bloc