INSERT INTO `wp_posts` VALUES (187,6,'2019-06-03 09:17:04','2019-06-03 07:17:04','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour cette semaine, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Plat du jour: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi: <em>Salade  au chou kale</em></strong> (courgettes*, tomates cerises*, abricots, salade verte*, chou kale du jardin, parmesan, oeuf° mollet ou poché, sauce caesar végétale aux amandes°, herbes du jardin et de chez Ludivine) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi: Poulet yassa</strong> (Poulet°, citron°, oignons*, riz, mélange de légumes*, cacahuètes° torréfiées)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi: nouilles thaï au boeuf </strong>(nouilles de riz, boeuf grillé émincé et mariné, légumes*, sésames°, cacahuètes torréfiées)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi:  <em>buddha bowl</em> </strong>(au choix: truite fumée au citron ou poulet grillé aux épices, trio de quinoa° ou riz vinaigré, légumes* - crus et rôtis aux épices) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Tarte du moment:</strong> Tarte feuilletée amandes tomates*, velouté de carottes* et patates douces°, salade verte*, feta</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert du moment:</strong> mardi et mercredi: cake à la patate douce/pommes poelées aux épices et citron - jeudi et vendredi: salade de fruits de saison et faisselle façon crumble </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>*produits issus d\'une agriculture locale et raisonnée (Ludivine et Jérôme, Cucurron)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>°produits issus de l\'agriculture biologique</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[\"17\",\"155\",194],\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-3 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/03/salade-abricots-courgette-tomates3-576x1024.jpg\" alt=\"\" data-id=\"17\" data-link=\"http://lherbefolle.net/salade-abricots-courgette-tomates3/\" class=\"wp-image-17\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/05/P5211081-1024x1024.jpg\" alt=\"\" data-id=\"155\" data-link=\"http://lherbefolle.net/index.php/2019/05/22/menu-du-22-mai/non-classe/attachment/olympus-digital-camera-28/\" class=\"wp-image-155\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/P6223634-1024x576.jpg\" alt=\"\" data-id=\"194\" data-link=\"http://lherbefolle.net/index.php/2019/06/03/menu-du-3-juin-2019/non-classe/attachment/p6223634/\" class=\"wp-image-194\"/></figure></li></ul></figure>\n<!-- /wp:gallery -->','Menu du 11 au 14 juin 2019','','publish','open','open','','menu-du-3-juin-2019','','','2021-04-28 11:37:20','2021-04-28 11:37:20','',0,'http://lherbefolle.net/?p=187',0,'post','',0);
INSERT INTO `wp_posts` VALUES (198,6,'2019-06-17 09:20:38','2019-06-17 07:20:38','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, pour préparer une fête de la musique internationale, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Tarte de la semaine: Tarte amandes/tomates</strong>, velouté de carottes aux herbes fraîches, petite salade </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert de la semaine: comme un crumble </strong>aux fraises et cerises</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi, c\'est japonais : Aubergines* à la vapeur</strong>, sésame et oignons de printemps* (servies avec riz et tofu frit)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi, c\'est thaïlandais : Curry laksa </strong>(soupe de nouilles au lait de coco, feuilles de combawa et légumes*) au poulet° ou végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi, c\'est indien: Ratatouille à l\'indienne</strong> (légumes* - pommes de terre, poivrons, haricots verts, tomates, oignons,...), servi avec du riz et un paneer naan</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi, c\'est italien: pâtes aux herbes du jardin</strong>, parmesan et légumes* rôtis </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi, c\'est américain </strong>(ou tout comme)<strong>: burger de patates douces</strong>° et mozzarella d\'Aix</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Les plats sont accompagnés de salade (verte ou composée selon les jours et les alliances de goûts)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[203,206,211],\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-3 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019