INSERT INTO `wp_posts` VALUES (1088,1,'2021-04-28 11:38:59','2021-04-28 11:38:59','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cette semaine, pour préparer une fête de la musique internationale, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Tarte de la semaine: Tarte amandes/tomates</strong>, velouté de carottes aux herbes fraîches, petite salade </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert de la semaine: comme un crumble </strong>aux fraises et cerises</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi, c\'est japonais : Aubergines* à la vapeur</strong>, sésame et oignons de printemps* (servies avec riz et tofu frit)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mardi, c\'est thaïlandais : Curry laksa </strong>(soupe de nouilles au lait de coco, feuilles de combawa et légumes*) au poulet° ou végétarien</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[\"203\",\"206\",211],\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-3 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20181123_093607-1024x576.jpg\" alt=\"\" data-id=\"203\" data-link=\"http://lherbefolle.net/index.php/2019/06/17/menu-du-17-au-19-juin-2019/non-classe/attachment/20181123_093607/\" class=\"wp-image-203\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20190613_090521-1024x1024.jpg\" alt=\"\" data-id=\"206\" data-link=\"http://lherbefolle.net/index.php/2019/06/17/menu-du-17-au-19-juin-2019/non-classe/attachment/20190613_090521/\" class=\"wp-image-206\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/06/20190618_122109-1024x1024.jpg\" alt=\"\" data-id=\"211\" data-link=\"http: