INSERT INTO `wp_posts` VALUES (1092,1,'2021-04-28 11:49:01','2021-04-28 11:49:01','<!-- wp:paragraph -->\n<p>Bonjour à tous!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour vous soutenir dans cette semaine caniculaire, l\'Herbe Folle vous propose:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Lundi et Mardi, c\'est salade</strong> : <em> Salade de crudités (</em>courgettes, carottes, salade verte, abricots, concombre, herbes du jardin), mozzarella, truite fumée ou poulet rôtis, sauce au yaourt grec et à la menthe</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Mercredi,  c\'est salade aussi : </strong><em>Salade thaï</em> (légumes crus marinés, nouilles de riz, herbes fraîches) au bœuf ou végétarienne (tofu grillé)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Jeudi, c\'est salade :</strong> Trio de quinoa, légumes crus et cuits, poulet grillé aux épices</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Vendredi, c\'est salade aussi :</strong> <em>Aubergines vapeur </em>à la japonaise, tofu au miso, nouilles soba - accompagné de salade verte aux sésame</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Dessert de la semaine</strong>: <em>Salade de fraises et abricots </em>au basilic, crème au chocolat ou tartelettes crème d\'amandes/abricots</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[86,222,221],\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery columns-3 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://lherbefolle.net/wp-content/uploads/2019/04/P4110503-1024x576.jpg\" alt=\"\" data-id=\"86\" data-link=\"http://lherbefolle.net/index.php/2019/04/17/menu-du-17-avril-2019/non-classe/attachment/p4110503/\" class=\"wp-image-86\"/><figcaption class=\"blocks-gallery-item__caption\">petit rayon de soleil matinal dans le jardin de l\'HerbeFolle</figcaption></figure></li><li class=\"blocks-gallery-ite